import React, { useState } from "react";
import { useRef, useEffect } from "react";
import emailjs from 'emailjs-com';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Image from 'react-bootstrap/Image';
import "bootstrap/dist/css/bootstrap.css";
import Card from 'react-bootstrap/Card';
import Badge from 'react-bootstrap/Badge';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import "./App.css";
import { motion } from "framer-motion"


function App() {

  const [isIntersecting, setIsIntersecting] = useState(false);
  const ref = useRef(null);


  function sendEmail(e) {
    e.preventDefault();    //This is important, i'm not sure why, but the email won't send without it

    emailjs.sendForm('service_8y585qi', 'template_rlv171e', e.target, 'LS0KcXi3uPgexxbgJ')
      .then((result) => {
        window.location.reload()  //This is if you still want the page to reload (since e.preventDefault() cancelled that behavior) 
      }, (error) => {
        console.log(error.text);
      });
  }

  const [formData, setFormData] = useState({
    username: "",
    password: "",
    errors: {},
    loading: false,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({ ...prevState, [name]: value }));
  };

  const validateForm = () => {
    const errors = {};

    // Check if username is empty
    if (!formData.username) {
      errors.username = "Username is required";
    }

    // Check if password is empty
    if (!formData.password) {
      errors.password = "Password is required";
    }

    setFormData((prevState) => ({ ...prevState, errors }));
    // Return true if there are no errors
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    setFormData({
      ...formData,
      loading: true,
    });

    // Simulate form submission delay
    setTimeout(() => {
      console.log(formData);
      setFormData({
        ...formData,
        loading: false,
      });
    }, 2000);
  };

  const divStyle = {
    width: '100vw',
    //background-size: cover,
    backgroundPosition: 'center',
    height: 500,
    //flex: 1,
    //width: null
    //width: '88%',
    //height: '800px',
    //backgroundImage: `url(${imgMyimageexample})`,
    //backgroundSize: 'cover'   <---- This is important
  };


  const scrollRef = useRef(null)
  /*const transition = {
    delay: 0.2
  }*/
  return (
    <>
      <div className="MainContainer">
        <Navbar bg="dark" data-bs-theme="dark" sticky="top">
          <Container>
            <Navbar.Brand href="#home">PATEL CONSULTING SERVICES INC</Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end"></Navbar.Collapse>
            <Nav className="me-auto">
              <Nav.Link href="#aboutus">About us</Nav.Link>
              <Nav.Link href="#services">Services</Nav.Link>
              <Nav.Link href="#reviews">Reviews</Nav.Link>
              <Nav.Link href="#contact">Contact</Nav.Link>
            </Nav>
          </Container>
        </Navbar>

        <div>
          <Image src={"house-image.jpg"} style={divStyle} fluid />
          <br /><br />
        </div>


        <div>
          <motion.div
            animate={{ x: 100 }}
            transition={{ type: "spring", stiffness: 100, delay: 0.3 }}
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
          //viewport={{ root: scrollRef }}
          >
            <div class="row" style={{ padding: '0.5rem' }}>
              <div class="col-auto"><Badge bg="success" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="info" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="warning" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="danger" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
            </div>
            <div class="row" style={{ padding: '0.5rem' }}>
              <div class="col-auto"><Badge bg="success" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="info" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="warning" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="warning" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
            </div>
            <div class="row" style={{ padding: '0.5rem' }}>
              <div class="col-auto"><Badge bg="success" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="info" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="warning" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
              <div class="col-auto"><Badge bg="warning" style={{ padding: '1.0rem', fontSize: 20 }}>Bookkeeping</Badge></div>
            </div>
          </motion.div>
          <br />
        </div>

        <ReactGoogleReviews layout="carousel" featurableId={featurableWidgetId} />


        <Card className="CardFormatting" >
          <Card.Header><h4>Contact Us</h4></Card.Header>
          <Card.Body>
            <Form onSubmit={sendEmail} className="FormInfo">
              <Row className="mb-3" >

                {formData.errors.from_name && (
                  <p style={{ color: "red" }}>{formData.errors.from_name}</p>
                )}
                <Form.Group as={Col} controlId="formGridName">
                  <Form.Label>Name</Form.Label>
                  <Form.Control type="text" placeholder="Name" name="from_name" value={formData.from_name} onChange={handleChange} />
                </Form.Group>

                {formData.errors.from_email && (
                  <p style={{ color: "red" }}>{formData.errors.from_email}</p>
                )}
                <Form.Group as={Col} controlId="formGridEmail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control type="email" placeholder="Email" name="from_email" value={formData.from_email} onChange={handleChange} />
                </Form.Group>

              </Row>
              <Row>
                <Form.Group className="mb-3" controlId="formGridMessage">
                  <Form.Label>Message</Form.Label>
                  <Form.Control placeholder="Message" as="textarea" name="message" value={formData.message} onChange={handleChange} />

                  {formData.errors.message && (
                    <p style={{ color: "red" }}>{formData.errors.message}</p>
                  )}
                </Form.Group>
              </Row>
              <Button variant="info">Submit</Button>

            </Form>
          </Card.Body>
        </Card>
      </div>
    </>
  );
}
export default App;
