import React, { useState } from "react";
import { useRef, useEffect } from "react";
import emailjs from 'emailjs-com';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Image from 'react-bootstrap/Image';
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import Badge from 'react-bootstrap/Badge';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import ListGroup from 'react-bootstrap/ListGroup';
import "./App.css";
import { motion } from "framer-motion";


function App() {

  const [isIntersecting, setIsIntersecting] = useState(false);
  const ref = useRef(null);


  function sendEmail(e) {
    e.preventDefault();    //This is important, i'm not sure why, but the email won't send without it

    emailjs.sendForm('service_8y585qi', 'template_rlv171e', e.target, 'LS0KcXi3uPgexxbgJ')
      .then((result) => {
        window.location.reload()  //This is if you still want the page to reload (since e.preventDefault() cancelled that behavior) 
      }, (error) => {
        console.log(error.text);
      });
  }

  const [formData, setFormData] = useState({
    username: "",
    password: "",
    errors: {},
    loading: false,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({ ...prevState, [name]: value }));
  };

  const validateForm = () => {
    const errors = {};

    // Check if username is empty
    if (!formData.username) {
      errors.username = "Username is required";
    }

    // Check if password is empty
    if (!formData.password) {
      errors.password = "Password is required";
    }

    setFormData((prevState) => ({ ...prevState, errors }));
    // Return true if there are no errors
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    setFormData({
      ...formData,
      loading: true,
    });

    // Simulate form submission delay
    setTimeout(() => {
      console.log(formData);
      setFormData({
        ...formData,
        loading: false,
      });
    }, 2000);
  };

  const divStyle = {
    width: '100vw',
    //background-size: cover,
    backgroundPosition: 'center',
    height: 500,
    //flex: 1,
    //width: null
    //width: '88%',
    //height: '800px',
    //backgroundImage: `url(${imgMyimageexample})`,
    //backgroundSize: 'cover'   <---- This is important
  };


  function getStars(rating) {
    const stars = [];
    for (let i = 0; i < 5; i++) {
      if (rating - 1 < i) {
        stars.push(<span>☆</span>);
      } else {
        stars.push(<span>★</span>);
      }
    }
    return stars;
  }

  const RATING_DATA = [
    { name: 'Manga1', rating: 1, comment: 'It is good' },
    { name: 'Manga2', rating: 4, comment: 'It is great' },
    { name: 'Manga3', rating: 5, comment: 'It is fantastic' },
    { name: 'Manga4', rating: 0, comment: 'It is brilliant' },
    { name: 'Manga5', rating: 2, comment: 'It is bravo' },
  ];

  const scrollRef = useRef(null)
  /*const transition = {
    delay: 0.2
  }*/
  return (
    <>
      <div className="MainContainer">
        <Navbar bg="dark" data-bs-theme="dark" sticky="top">
          <Container>
            <Navbar.Brand href="#home">PATEL CONSULTING SERVICES INC</Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end"></Navbar.Collapse>
            <Nav className="me-auto">
              <Nav.Link href="#aboutus">About us</Nav.Link>
              <Nav.Link href="#services">Services</Nav.Link>
              <Nav.Link href="#reviews">Reviews</Nav.Link>
              <Nav.Link href="#contact">Contact</Nav.Link>
            </Nav>
          </Container>
        </Navbar>

        <div>
          <Image src={"house-image-2.jpg"} style={divStyle} fluid />
          <br /><br />
        </div>



        <Card className="CardFormatting" >
        <Card.Header><h2>Our Services</h2></Card.Header>
        <div>
            <div class="flexbox-container">
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#b76e79', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Real Estate</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#6cb7ab', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Overseas Money Transfer</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#4f4036', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>CAR Financing</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#915c83', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Income Tax</Badge></div>
            </div>
            <div class="flexbox-container">
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#4f4036', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Business Registration</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#915c83', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Bookkeeping</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#b76e79', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Business Tax</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#6cb7ab', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Visa Application</Badge></div>
            </div>
            <div class="flexbox-container">
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#6cb7ab', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Passport Application</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#b76e79', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Mortgage</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#915c83', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Payroll Services</Badge></div>
              <div class="col-auto" style={{ padding: '1.7rem' }}><Badge bg="" style={{ backgroundColor: '#4f4036', paddingRight: '3rem', paddingLeft: '3rem', paddingTop: '1rem', paddingBottom: '1rem',fontSize: 25}}>Fixed/Variable LOC</Badge></div>
            </div>
          <br />
        </div>
        </Card>

        <Card className="CardFormatting" >
        <Card.Header><h2>What do our customers think?</h2></Card.Header>
        <div class="flexbox-container">
          <div  style={{ padding: '1.0rem' }}>
          <ListGroup >
            <ListGroup.Item
              as="li"
              className="d-flex justify-content-between align-items-start"
              style={{ margin: '1.0rem', backgroundColor: '#556d7c', color: 'white' }}
            >
              <div className="ms-2 me-auto">
                <div className="fw-bold">Moamen Elhendawi</div>
                Patel’s service is unmatched. He was extremely patient with me and he took the time to make sure that 
                I get the most money that I can get back. Would recommend him to anyone who’s new to filing for tax return!!
              </div>
              <Badge bg="warning" pill>
                {getStars(5)}
              </Badge>
            </ListGroup.Item>
            <br />
            <ListGroup.Item
              as="li"
              className="d-flex justify-content-between align-items-start"
              style={{ margin: '1.0rem', backgroundColor: '#b76e79', color: 'white'  }}
            >
              <div className="ms-2 me-auto">
                <div className="fw-bold">Anthony Racine</div>
                Musa has been extremely helpful, responsive, diligent, and honest 
                during the whole process of finding an condo apartment. Me and my wife are very thankful for his service.
              </div>
              <Badge bg="warning" pill>
                {getStars(5)}
              </Badge>
            </ListGroup.Item>

          </ListGroup>
          </div>
          <div style={{ padding: '1.0rem' }}>
          <ListGroup>
          <ListGroup.Item
              as="li"
              className="d-flex justify-content-between align-items-start"
              style={{ margin: '1.0rem', backgroundColor: '#4f4036', color: 'white'  }}
            >
              <div className="ms-2 me-auto">
                <div className="fw-bold">Clovis Obedi</div>
                Had a great experience with Patel, he took the time to make sure all my tax return were accounted for 
                and all my files were up to date. Highly recommend him for a great service and professional work.
              </div>
              <Badge bg="warning" pill>
                {getStars(5)}
              </Badge>
            </ListGroup.Item>
            <br />
            <ListGroup.Item
              as="li"
              className="d-flex justify-content-between align-items-start"
              style={{ margin: '1.0rem', backgroundColor: '#4f4036', color: 'white'  }}
            >
              <div className="ms-2 me-auto">
                <div className="fw-bold">Kristel Caressa Diaz</div>
                Working with Moses Patel on my tax return was a great experience! He was knowledgeable, 
                thorough, and made sure everything was done correctly. I highly recommend his tax services.
              </div>
              <Badge bg="warning" pill>
                {getStars(5)}
              </Badge>
            </ListGroup.Item>
            </ListGroup>
            </div>
            <div style={{ padding: '1.0rem' }}>
          <ListGroup>
          <ListGroup.Item
              as="li"
              className="d-flex justify-content-between align-items-start"
              style={{ margin: '1.0rem', backgroundColor: '#4f4036', color: 'white'  }}
            >
              <div className="ms-2 me-auto">
                <div className="fw-bold">Jay Patel</div>
                Mr. Musa is very professional, detail orientated and quick at work. More than 100% recommend for services.
              </div>
              <Badge bg="warning" pill>
                {getStars(5)}
              </Badge>
            </ListGroup.Item>
            <br />
            <ListGroup.Item
              as="li"
              className="d-flex justify-content-between align-items-start"
              style={{ margin: '1.0rem', backgroundColor: '#4f4036', color: 'white'  }}
            >
              <div className="ms-2 me-auto">
                <div className="fw-bold">Firoz Mahmud</div>
                Mr Musa Patel is an outstanding real estate agent. In our busy schedule, he managed almost everything, so that nothing 
                unexpected comes out of the blue at the last moment. We closed our deal less than the listed price and all credit goes 
                to him, who negotiated very carefully and submitted the offer.
              </div>
              <Badge bg="warning" pill>
                {getStars(5)}
              </Badge>
            </ListGroup.Item>
            <br />
            <ListGroup.Item
              as="li"
              className="d-flex justify-content-between align-items-start"
              style={{ margin: '1.0rem', backgroundColor: '#4f4036', color: 'white'  }}
            >
              <div className="ms-2 me-auto">
                <div className="fw-bold">Lucy Dellaterza</div>
                Great customer skills.  Has fantastic abilities to assist anyone with their taxes.  Very quick and seamless.  I highly recommend!
              </div>
              <Badge bg="warning" pill>
                {getStars(5)}
              </Badge>
            </ListGroup.Item>

            </ListGroup>

            </div>

        </div>
        </Card>


        <Card className="CardFormatting" >
          <Card.Header><h2>Let's connect!</h2></Card.Header>
          <Card.Body>
          <div className="contact" style={{color: 'white', fontSize: '18px' }}>
          <Button variant="info" style={{color: 'white', fontSize: '18px' }} className='contactButtons' onClick={() => window.location = 'mailto:patel.accounting@gmail.com'}>
            Send an Email</Button>
          </div>
          <br />
          <p className="contact" style={{ marginRight: '1.0rem', marginLeft: '1.0rem', color: 'white', fontSize: '13px' }}> 
            Or reach out to us through - </p>
            <h6 className="contact" style={{ marginRight: '1.0rem', marginLeft: '1.0rem', color: 'white' }}> 
            Tel No : +1 (647) 717-9611</h6>
            <h6 className="contact" style={{ marginRight: '1.0rem', marginLeft: '1.0rem', color: 'white' }}> 
            Email : patel.accounting@gmail.com</h6>

          </Card.Body>
        </Card>
      </div>
    </>
  );
}
export default App;
